import React, {useState} from "react";
import {useDispatch} from "react-redux";
import styled from "styled-components";
import {logUser} from "../actions";

const DivLog  = styled.div`
    border      : 1px;
    padding     : 5px;
`;

const Log = (props) => {
    const dispatch = useDispatch();
    const [username, setUsername] = useState("");

    const catchLogin = (e) => {
        e.preventDefault();

        if (username) {
            dispatch(logUser(username, "Moi"))
        }
    };

    const inputChange = (e) => {
        setUsername(e.value.target);

    };

    return (
        <DivLog className={ "card" }>
            <label>
                <small>Username :</small>
            </label>
            <input type={ "text" } value={ username } onChange={ inputChange }/>
            <input type={ "submit" } onClick={ catchLogin }>Login</input>
        </DivLog>
    );
};

export default Log;
