import {useDispatch} from "react-redux";
import {ADD_MESSAGE, LOAD_MESSAGE_PENDING, LOAD_MESSAGE_ERROR, LOAD_MESSAGE_SUCCESS} from "../actions";
import axios from "axios";

const URL = "https://my-json-server.typicode.com/tlenclos/formation-react-fake-server/messages";

const INIT_STATE = () => {
    const dispatch = useDispatch();

   // return () => {
     //   dispatch({type: LOAD_MESSAGE_PENDING});

        return async () => {
             await axios.get(URL).then(
                response => {
                    console.log(response);
                    dispatch({type :LOAD_MESSAGE_SUCCESS, messages: response})
                }
            ).catch(err => dispatch({type :LOAD_MESSAGE_ERROR, error: err}))
        }
    //}
};

export const messageList = (state = INIT_STATE, action) => {
    console.log(action);
    switch (action.type) {
        case ADD_MESSAGE :
            let newState = {
                username: action.username,
                message : action.message,
                sentAt  : action.sentAt,
            };

            return [...state, newState];

        case LOAD_MESSAGE_ERROR :
            return state;

        case LOAD_MESSAGE_PENDING :
            return state;

        case LOAD_MESSAGE_SUCCESS :
            return state;

        default :
            return state;
    }
};
