import {ADD_MESSAGE, LOAD_MESSAGE_PENDING, LOAD_MESSAGE_ERROR, LOAD_MESSAGE_SUCCESS} from "../actions"

const INIT_STATE = {
        messages : [
            {
                message: 'hello word',
                username: 'toto',
                sentAt  : new Date()
            }
        ]
    }
;

export const messageList = (state = INIT_STATE, action) => {
    switch (action.type) {
        case ADD_MESSAGE :
            let newState = {
                message : action.message,
                username: action.username,
                sentAt  : action.sentAt,
            };

            return [...state.messages, newState];

        case LOAD_MESSAGE_PENDING:
            return state;

        case LOAD_MESSAGE_SUCCESS:
              const h =  [...state.messages, state.messages.push(action.messages)]


            return h ;

        case LOAD_MESSAGE_ERROR:
            return [...state.messages, {
                message : action.error,
                username: "system",
                sentAt  : new Date(),
            }];

        default :
            return state;
    }
};
