import React from "react";
import MessageList from "./MessageList";
import MessageBar from "./MessageBar";
import styled from "styled-components";
import {useSelector} from "react-redux";
import {Redirect} from "react-router-dom";
import axios from "axios";
const DivChat  = styled.div`
    border      : 1px;
    padding     : 5px;
`;
const Welcome  = styled.div`
    border      : 1px;
    padding     : 5px;
`;

const URL = "https://my-json-server.typicode.com/tlenclos/formation-react-fake-server/messages";

export const getMessages = () => {
    return (dispatch) => {
        dispatch({      type: 'LOAD_MESSAGES_PENDING'    });
        return axios.get(URL)
            .then((json) => {
                dispatch({type: 'LOAD_MESSAGES_SUCCESS', messages: json.data});
            }).catch((err) => {
                dispatch({type: 'LOAD_MESSAGES_ERROR', error: err});
            });
    }
};

const Chat = () => {
    const username = useSelector(state => state.username);

    if (!username) {
        return <Redirect to="/" />
    }
    return (
        <DivChat className = {"card container"}>
            <Welcome > Welcome Guys </Welcome>

            <form className = {"container"}>
                <MessageList />
                <MessageBar />
            </form>
        </DivChat>
    );
};

export default Chat;
