import {ADD_MESSAGE, LOAD_MESSAGE_PENDING} from "../actions"

const INIT_STATE = ['@EnDur ¬ Hello world'];

export const messageList = (state = INIT_STATE, action) => {

    switch (action.type) {
        case ADD_MESSAGE :
            return [...state, `@${action.username} ¬ ${action.message}`];

        case LOAD_MESSAGE_PENDING :
            return state;
        default :
            return state;
    }
};
