import {store} from "../";
import {
    ADD_MESSAGE,
    LOAD_MESSAGE_PENDING,
    LOAD_MESSAGE_ERROR,
    LOAD_MESSAGE_SUCCESS,
    pendingMessages,
    successMessages,
    errorMessages,
} from "../actions";
import axios from "axios";
import {forEach} from "react-bootstrap/cjs/ElementChildren";

const URL = "https://my-json-server.typicode.com/tlenclos/formation-react-fake-server/messages";

const INIT_STATE = () => {

    return axios.get(URL)
        .then(response => {
            store.dispatch({type : LOAD_MESSAGE_SUCCESS , message :response.data});
        })
        .catch(error => {
            store.dispatch({type : LOAD_MESSAGE_ERROR , message :error});
        })
};

export const messageList = (state = INIT_STATE(), action) => {
    switch (action.type) {
        case ADD_MESSAGE :
            let newState = {
                username: action.username,
                message : action.message,
                sentAt  : action.sentAt,
            };

            return [...state, newState];

        case LOAD_MESSAGE_ERROR :
            let errorState = {
                username: "",
                message : action.message,
                sentAt  : new Date(),
            };

            return [...state, errorState];

        case LOAD_MESSAGE_PENDING :
            return state;

        case LOAD_MESSAGE_SUCCESS :
            state = action.message.map( (msg) =>{
               return msg
            });

            return state;

        default :
            return state;
    }
};
