import {store} from "../";
import {
    ADD_MESSAGE,
    LOAD_MESSAGE_PENDING,
    LOAD_MESSAGE_ERROR,
    LOAD_MESSAGE_SUCCESS,
    successMessages,
    pendingMessages,
    errorMessages,
} from "../actions";
import axios from "axios";

const URL = "https://my-json-server.typicode.com/tlenclos/formation-react-fake-server/messages";

const INIT_STATE = () => {
    return (dispatch) => {


    return axios.get(URL)
        .then(response => {
            store.dispatch(successMessages(response.data));
        }).then(
            response => {
                store.dispatch(pendingMessages('...chargement'));
            }
        )
        .catch(error => {
            store.dispatch(errorMessages(error));
        })
    }
};

export const messageList = (state = INIT_STATE(), action) => {
    switch (action.type) {
        case ADD_MESSAGE :
            let newState = {
                username: action.username,
                message : action.message,
                sentAt  : action.sentAt,
            };

            return [...state, newState];

        case LOAD_MESSAGE_ERROR :
            console.log(state);
            /*const errorState = () => ({
                username: "error",
                message : action.message,
                sentAt  : new Date(),
            });
            console.log(errorState());
            return errorState();*/
            return state;

        case LOAD_MESSAGE_PENDING :
            return [...state, action.message];

        case LOAD_MESSAGE_SUCCESS :
            state = action.message.map( (msg) =>{
               return msg
            });
            return state;

        default :
            console.log(state)
            return state;
    }
};
